#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="D:\gssoc october\Appointment-booking-app\backend\node_modules\.pnpm\nodemon@3.1.7\node_modules\nodemon\bin\node_modules;D:\gssoc october\Appointment-booking-app\backend\node_modules\.pnpm\nodemon@3.1.7\node_modules\nodemon\node_modules;D:\gssoc october\Appointment-booking-app\backend\node_modules\.pnpm\nodemon@3.1.7\node_modules;D:\gssoc october\Appointment-booking-app\backend\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/d/gssoc october/Appointment-booking-app/backend/node_modules/.pnpm/nodemon@3.1.7/node_modules/nodemon/bin/node_modules:/mnt/d/gssoc october/Appointment-booking-app/backend/node_modules/.pnpm/nodemon@3.1.7/node_modules/nodemon/node_modules:/mnt/d/gssoc october/Appointment-booking-app/backend/node_modules/.pnpm/nodemon@3.1.7/node_modules:/mnt/d/gssoc october/Appointment-booking-app/backend/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../nodemon/bin/nodemon.js" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../nodemon/bin/nodemon.js" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../nodemon/bin/nodemon.js" $args
  } else {
    & "node$exe"  "$basedir/../nodemon/bin/nodemon.js" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
